mapper.select:
SELECT
`id`,`title`,`url`,`icon`,`leavel`,`router_type`,`parent_router`,`create_time`,`create_personal`,`del_time`,`del_personal`,`status`
FROM `ic_util_router_navigation` WHERE
    (`leavel` = 0
        OR `leavel` <= (
            SELECT
            `leavel` FROM `ic_user_leavel` WHERE `user_type` = (
            SELECT `register_identify` FROM `ic_main_user` WHERE
            `easy_id` = #{uid}
            AND `node_status` = '1'
            AND `user_status` = '1'
            )
            )

    )
    AND `router_type` = #{router_type}
    AND `status` = 1 ORDER BY `parent_router`
